export const TechStack = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: '-100px' });

  // Duplicate for seamless loop
  const duplicatedTech = [...techStack, ...techStack];

  return (
    <section id="tech-stack" className="relative py-24 overflow-hidden" ref={ref}>
      <div className="absolute inset-0 bg-dot-pattern opacity-20" />

      <div className="container relative z-10 mx-auto px-4 md:px-8">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.5 }}
          className="text-center mb-12"
        >
          <span className="text-primary font-mono text-sm uppercase tracking-wider">Technologies</span>
          <h2 className="text-4xl md:text-5xl font-bold mt-4 mb-6">
            Tech <span className="gradient-text">Stack</span>
          </h2>
          <div className="section-divider" />
        </motion.div>

        {/* Animated scrolling rows */}
        <div className="space-y-8">
          {/* Row 1 - scroll left */}
          <div className="relative overflow-hidden">
            <motion.div
              className="flex gap-6"
              animate={{ x: [0, -50 * techStack.length] }}
              transition={{
                x: {
                  repeat: Infinity,
                  repeatType: "loop",
                  duration: 30,
                  ease: "linear",
                },
              }}
            >
              {duplicatedTech.map((tech, index) => (
                <motion.div
                  key={`row1-${index}`}
                  className="flex-shrink-0 group"
                  whileHover={{ scale: 1.2, y: -5 }}
                >
                  <div 
                    className="w-16 h-16 rounded-2xl bg-card/80 backdrop-blur-sm border border-border flex items-center justify-center transition-all duration-300 group-hover:border-transparent"
                    style={{ 
                      boxShadow: 'none',
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.boxShadow = `0 0 30px ${tech.color}60, 0 0 60px ${tech.color}30`;
                      e.currentTarget.style.borderColor = tech.color;
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.boxShadow = 'none';
                      e.currentTarget.style.borderColor = '';
                    }}
                  >
                    <TechIcon name={tech.name} color={tech.color} />
                  </div>
                </motion.div>
              ))}
            </motion.div>
          </div>

          {/* Row 2 - scroll right */}
          <div className="relative overflow-hidden">
            <motion.div
              className="flex gap-6"
              animate={{ x: [-50 * techStack.length, 0] }}
              transition={{
                x: {
                  repeat: Infinity,
                  repeatType: "loop",
                  duration: 35,
                  ease: "linear",
                },
              }}
            >
              {[...duplicatedTech].reverse().map((tech, index) => (
                <motion.div
                  key={`row2-${index}`}
                  className="flex-shrink-0 group"
                  whileHover={{ scale: 1.2, y: -5 }}
                >
                  <div 
                    className="w-16 h-16 rounded-2xl bg-card/80 backdrop-blur-sm border border-border flex items-center justify-center transition-all duration-300 group-hover:border-transparent"
                    style={{ 
                      boxShadow: 'none',
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.boxShadow = `0 0 30px ${tech.color}60, 0 0 60px ${tech.color}30`;
                      e.currentTarget.style.borderColor = tech.color;
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.boxShadow = 'none';
                      e.currentTarget.style.borderColor = '';
                    }}
                  >
                    <TechIcon name={tech.name} color={tech.color} />
                  </div>
                </motion.div>
              ))}
            </motion.div>
          </div>
        </div>
      </div>
    </section>
  );
};
